import styled from "styled-components";



const Input = styled.input`
    width: 300px;
    height: 50px;
    border: 1px solid #333;
    padding: 10px 30px;
    opacity: 0.3;
    &:focus{
        border: 1px solid #ace;
        opacity: 1;
    }
`

export default Input
