import styled from "styled-components";

const Input = styled.input`
    border: 2px solid #eeeeee;
    border-radius: 4px;
    outline: none;
    width: 300px;
    height: 50px;
    padding: 10px 30px;
    opacity: 0.3;
    &:focus{
        border: 1px solid red;
        opacity: 1;
    }
`

export default Input
